TeCanALRet Marshal_BMSBroadcast(uint8_t *TxData) {
	uint64_t dataWord = 0;
	TsBMSBroadcast temp;
	TeCanALRet ret;

	temp = BMSBroadcast;
    
    // Reverse linear conversion
	temp.ThermModuleNum = (temp.ThermModuleNum
			- BMSBROADCAST_THERMMODULENUM_OFFSET)
			/ BMSBROADCAST_THERMMODULENUM_FACTOR;
            
	temp.LowThermValue = (temp.LowThermValue
			- BMSBROADCAST_LOWTHERMVALUE_OFFSET)
			/ BMSBROADCAST_LOWTHERMVALUE_FACTOR;
            
	temp.HighThermValue = (temp.HighThermValue
			- BMSBROADCAST_HIGHTHERMVALUE_OFFSET)
			/ BMSBROADCAST_HIGHTHERMVALUE_FACTOR;
            
	temp.AvgThermValue = (temp.AvgThermValue
			- BMSBROADCAST_AVGTHERMVALUE_OFFSET)
			/ BMSBROADCAST_AVGTHERMVALUE_FACTOR;
            
	temp.NumThermEn = (temp.NumThermEn
			- BMSBROADCAST_NUMTHERMEN_OFFSET)
			/ BMSBROADCAST_NUMTHERMEN_FACTOR;
            
	temp.HighThermID = (temp.HighThermID
			- BMSBROADCAST_HIGHTHERMID_OFFSET)
			/ BMSBROADCAST_HIGHTHERMID_FACTOR;
            
	temp.LowThermID = (temp.LowThermID
			- BMSBROADCAST_LOWTHERMID_OFFSET)
			/ BMSBROADCAST_LOWTHERMID_FACTOR;
            
	temp.Checksum = (temp.Checksum
			- BMSBROADCAST_CHECKSUM_OFFSET)
			/ BMSBROADCAST_CHECKSUM_FACTOR;
            
    // CANAL_SET_BITS only writes to correct bits, dataWord is not being overwritten
	dataWord = CANAL_SET_BITS(temp.ThermModuleNum, dataWord,
            //little_endian
            BMSBROADCAST_THERMMODULENUM_START,
			BMSBROADCAST_THERMMODULENUM_LENGTH);
            
	dataWord = CANAL_SET_BITS(temp.LowThermValue, dataWord,
            //little_endian
            BMSBROADCAST_LOWTHERMVALUE_START,
			BMSBROADCAST_LOWTHERMVALUE_LENGTH);
            
	dataWord = CANAL_SET_BITS(temp.HighThermValue, dataWord,
            //little_endian
            BMSBROADCAST_HIGHTHERMVALUE_START,
			BMSBROADCAST_HIGHTHERMVALUE_LENGTH);
            
	dataWord = CANAL_SET_BITS(temp.AvgThermValue, dataWord,
            //little_endian
            BMSBROADCAST_AVGTHERMVALUE_START,
			BMSBROADCAST_AVGTHERMVALUE_LENGTH);
            
	dataWord = CANAL_SET_BITS(temp.NumThermEn, dataWord,
            //little_endian
            BMSBROADCAST_NUMTHERMEN_START,
			BMSBROADCAST_NUMTHERMEN_LENGTH);
            
	dataWord = CANAL_SET_BITS(temp.HighThermID, dataWord,
            //little_endian
            BMSBROADCAST_HIGHTHERMID_START,
			BMSBROADCAST_HIGHTHERMID_LENGTH);
            
	dataWord = CANAL_SET_BITS(temp.LowThermID, dataWord,
            //little_endian
            BMSBROADCAST_LOWTHERMID_START,
			BMSBROADCAST_LOWTHERMID_LENGTH);
            
	dataWord = CANAL_SET_BITS(temp.Checksum, dataWord,
            //little_endian
            BMSBROADCAST_CHECKSUM_START,
			BMSBROADCAST_CHECKSUM_LENGTH);
            
	setDataWordIntoByteArray(dataWord, TxData);

	return CANAL_OK;
}
