void Print_BMSBroadcast() {
	char* sep ="\n\r\t";

	CANAL_PRINT("MESSAGE: BMSBroadcast");
    CANAL_PRINT("%sThermModuleNum: %u", sep, BMSBroadcast.ThermModuleNum);
    CANAL_PRINT("%sLowThermValue: %d", sep, BMSBroadcast.LowThermValue);
    CANAL_PRINT("%sHighThermValue: %d", sep, BMSBroadcast.HighThermValue);
    CANAL_PRINT("%sAvgThermValue: %d", sep, BMSBroadcast.AvgThermValue);
    CANAL_PRINT("%sNumThermEn: %u", sep, BMSBroadcast.NumThermEn);
    CANAL_PRINT("%sHighThermID: %u", sep, BMSBroadcast.HighThermID);
    CANAL_PRINT("%sLowThermID: %u", sep, BMSBroadcast.LowThermID);
    CANAL_PRINT("%sChecksum: %d", sep, BMSBroadcast.Checksum);
    CANAL_PRINT("\n\r");
}
void Print_ThermistorBroadcast() {
	char* sep ="\n\r\t";

	CANAL_PRINT("MESSAGE: ThermistorBroadcast");
    CANAL_PRINT("%sRelThermID: %u", sep, ThermistorBroadcast.RelThermID);
    CANAL_PRINT("%sThermValue: %d", sep, ThermistorBroadcast.ThermValue);
    CANAL_PRINT("%sNumEnTherm: %d", sep, ThermistorBroadcast.NumEnTherm);
    CANAL_PRINT("%sLowThermValue: %d", sep, ThermistorBroadcast.LowThermValue);
    CANAL_PRINT("%sHighThermValue: %d", sep, ThermistorBroadcast.HighThermValue);
    CANAL_PRINT("%sHighThermID: %u", sep, ThermistorBroadcast.HighThermID);
    CANAL_PRINT("%sLowThermID: %u", sep, ThermistorBroadcast.LowThermID);
    CANAL_PRINT("\n\r");
}

TeCanALRet Print_Message(uint32_t *ID) {
	CanALPrinter* print;

	for (int i = 0; i < TOTAL_MESSAGES; i++) {
		if ((*ID) == CANAL_PRINTER_TABLE[i].ID) {
			print = CANAL_PRINTER_TABLE[i].printer;

			(*print)();

			return CANAL_OK;
		}
	}

	return CANAL_UNSUPPORTED_TX_MESSAGE;
}

TeCanALRet GetTxDataLength(TeMessageID *ID, uint32_t *dlc) {
	for (int i = 0; i < NUM_TX_MESSAGES; i++) {
			if ((*ID) == CANAL_TX_MESSAGE_TABLE[i].ID) {
				*dlc = CANAL_TX_MESSAGE_TABLE[i].dlc;

				return CANAL_OK;
			}
		}

    return CANAL_UNSUPPORTED_TX_MESSAGE;
}

// UnmarshalBinary acts as a generic binary unmarshaller
TeCanALRet UnmarshalBinary(uint32_t *ID, uint8_t* rawData) {
	BinaryUnmarshaller* unmarshal;
	TeCanALRet ret;
 
	if ((ret = getBinaryUnmarshaller(ID, &unmarshal)) != CANAL_OK)
		return ret;
 
	return (*unmarshal)(rawData);
}

// MarshalBinary acts as a generic binary marshaller
TeCanALRet MarshalBinary(TeMessageID *ID, uint8_t* txData) {
	BinaryMarshaller* marshal;
	TeCanALRet ret;
 
	if ((ret = getBinaryMarshaller(ID, &marshal)) != CANAL_OK) return ret;
 
	return (*marshal)(txData);
}

